mapper.insert:

    SELECT SEQ_CHAT.NEXTVAL FROM DUAL

INSERT INTO chat (
chat_id
, chat_room_id
, contents
, chat_type
, writer
, write_dt
, modifier
, modified_dt
, is_deleted
)
VALUES (
#{chatId}
, #{roomId}
, #{message}
, #{chatType}
, #{senderId}
, SYSDATE
, #{senderId}
, SYSDATE
, 0
)

mapper.select:
SELECT COUNT(*)
FROM chat
WHERE chat_type = #{type}
  AND writer = #{employeeId}

SELECT chat_room_id AS chatRoomId
FROM chat
WHERE chat_type = #{type}
  AND writer = #{employeeId}
  AND ROWNUM = 1
ORDER BY write_dt DESC
