mapper.update:
UPDATE chat_status
    SET is_emoticon = #{isEmoticon}
      , emoticon_type = #{emoticonType}
      , modifier = #{employeeId}
      , modified_dt = SYSDATE
    WHERE chat_id = #{chatId}
      AND employee_id = #{employeeId}

UPDATE chat_status
    SET is_read = 1
      , modifier = #{employeeId}
      , modified_dt = SYSDATE
    WHERE chat_room_id = #{chatRoomId}
      AND employee_id = #{employeeId}
      AND is_read = 0

mapper.select:
SELECT chat_id
    FROM chat_status
    WHERE chat_room_id = #{chatRoomId}
        AND employee_id = #{employeeId}
        AND is_read = 0
